mapper.select:
select id,res_name from rc_resource 
where status = '4'
and (
	
      res_name like #{keyWord1}
    
    
      or res_name like #{keyWord2}
    
    
      or res_name like #{keyWord3}
    
    
      or res_name like #{keyWord4}
    
    
      or res_name like #{keyWord5}
    
)

		SELECT * FROM
        (
		select * from rc_resource
		
            
                order by ${sidx} ${order}
            
			
                order by id desc
			
        ) PlayStation
		
			limit #{offset},#{limit}

mapper.update:
update rc_resource
version = #{version}, res_name = #{resName}, res_desc = #{resDesc}, res_type = #{resType}, cata_id = #{cataId}, org_id = #{orgId}, region_code = #{regionCode}, org_name = #{orgName}, share_type = #{shareType}, share_condition = #{shareCondition}, inner_share_type = #{innerShareType}, inner_share_condition = #{innerShareCondition}, open_type = #{openType}, open_condition = #{openCondition}, update_cycle = #{updateCycle}, custom_update_cycle = #{customUpdateCycle}, authz_type = #{authzType}, is_saved_to_platform = #{isSavedToPlatform}, create_method = #{createMethod}, creator_id = #{creatorId}, creator_name = #{creatorName}, create_time = #{createTime}, update_time = #{updateTime}, publish_time = #{publishTime}, status = #{status}, can_proxy = #{canProxy}, expire_time = #{expireTime}, from_system_id = #{fromSystemId}, from_system_name = #{fromSystemName}
where id = #{id}
