mapper.select:
		SELECT * FROM
        (
		select * from t_demand
		
            
                order by ${sidx} ${order}
            
			
                order by demand_id desc
			
        ) PlayStation
		
			limit #{offset},#{limit}

SELECT * FROM ( SELECT td.demand_id,td.demand_dep, dd.name AS demand_dep_name, td.provide_dep, pd.name AS provide_dep_name,td.demand_name,td.key_word,td.demand_detail, td.access_mode,am.par_name AS access_mode_name,td.serve_mode,sm.par_name AS serve_mode_name, td.frequency,f.par_name AS frequency_name,td.state,s.par_name AS state_name, td.demand_use,td.save_time,td.creater,td.remark, dr.choose_nums, dr.recommend_nums FROM t_demand td LEFT JOIN (SELECT COUNT(IF(state = 1, 1, NULL)) AS choose_nums,COUNT(resource_id) AS recommend_nums,demand_id FROM t_demand_resource GROUP BY demand_id) dr ON td.demand_id = dr.demand_id LEFT JOIN pub_organ dd ON td.demand_dep = dd.CODE AND dd.STATUS = '1' LEFT JOIN pub_organ pd ON td.provide_dep = pd.code AND pd.STATUS = '1' LEFT JOIN t_parameter am ON am.par_code = 'T_DEMAND|ACCESS_MODE' AND td.access_mode = am.par_value LEFT JOIN t_parameter sm ON sm.par_code = 'T_DEMAND|SERVE_MODE' AND td.serve_mode = sm.par_value LEFT JOIN t_parameter f ON f.par_code = 'T_DEMAND|FREQUENCY' AND td.frequency = f.par_value LEFT JOIN t_parameter s ON s.par_code = 'T_DEMAND|STATE' AND td.state = s.par_value
LEFT JOIN (SELECT demand_id,MAX(operate_time) AS operate_time FROM t_demand_operate WHERE state = #{timeType} GROUP BY demand_id) ope ON td.demand_id = ope.demand_id
WHERE 1=1
AND td.demand_dep = #{demandDep}
AND td.provide_dep = #{provideDep}
AND td.state = #{state}
AND ope.operate_time >= CONCAT(#{stratDate},' 00:00:00')
AND ope.operate_time <= CONCAT(#{endDate},' 23:59:59')
AND ((td.state = '00' AND td.creater = #{userId}) OR td.state NOT IN ('00'))
AND td.state NOT IN ('00')
ORDER BY td.state,td.save_time DESC) PlayStation
limit #{offset},#{limit}

SELECT count(td.demand_id) FROM t_demand td
LEFT JOIN (SELECT demand_id,MAX(operate_time) AS operate_time FROM t_demand_operate WHERE state = #{timeType} GROUP BY demand_id) ope ON td.demand_id = ope.demand_id
WHERE 1=1
AND td.demand_dep = #{demandDep}
AND td.provide_dep = #{provideDep}
AND td.state = #{state}
AND ope.operate_time >= CONCAT(#{stratDate},' 00:00:00')
AND ope.operate_time <= CONCAT(#{endDate},' 23:59:59')
AND ((td.state = '00' AND td.creater = #{userId}) OR td.state NOT IN ('00'))
AND td.state NOT IN ('00')

mapper.update:
update t_demand
demand_dep = #{demandDep}, provide_dep = #{provideDep}, demand_name = #{demandName}, key_word = #{keyWord}, demand_detail = #{demandDetail}, access_mode = #{accessMode}, serve_mode = #{serveMode}, frequency = #{frequency}, demand_use = #{demandUse}, save_time = #{saveTime}, creater = #{creater}, state = #{state}, remark = #{remark}
where demand_id = #{demandId}
