mapper.select:
SELECT * FROM ( SELECT h.period ,h.save_time,h.his_name,h.his_detail, am.par_name,pd.name,h.history_id,h.state FROM t_history_data h LEFT JOIN pub_organ pd ON h.provide_dep = pd.code and pd.STATUS='1' LEFT JOIN t_parameter am ON am.par_code = 'T_HISTORY_DATA|STATE' AND h.state = am.par_value
LEFT JOIN (SELECT history_id,MAX(operate_time) AS operate_time FROM t_history_operate WHERE state = #{timeType} GROUP BY history_id) ope ON h.history_id = ope.history_id
where 1=1
AND h.provide_dep = #{h_provideDep}
AND h.state = #{state}
AND ope.operate_time >= CONCAT(#{stratDate},' 00:00:00')
AND ope.operate_time <= CONCAT(#{endDate},' 23:59:59')
AND ((h.state = '00' AND h.creater = #{userId}) OR h.state NOT IN ('00'))
AND h.state NOT IN ('00')
) PlayStation
limit #{offset},#{limit}

		SELECT * FROM
        (
		select * from t_history_data
		
            
                order by ${sidx} ${order}
            
			
                order by history_id desc
			
        ) PlayStation
		
			limit #{offset},#{limit}

mapper.update:
update t_history_data
provide_dep = #{provideDep}, his_name = #{hisName}, his_detail = #{hisDetail}, period = #{period}, save_time = #{saveTime}, creater = #{creater}, state = #{state}, remark = #{remark}
where history_id = #{historyId}
