mapper.select:
		SELECT * FROM
        (
		select * from t_import_detail
		
            
                order by ${sidx} ${order}
            
			
                order by import_detail_id desc
			
        ) PlayStation
		
			limit #{offset},#{limit}

mapper.update:
update t_import_detail
import_id = #{importId}, demand_id = #{demandId}, provide_dep = #{provideDep}, provide_dep_name = #{provideDepName}, access_mode = #{accessMode}, access_mode_name = #{accessModeName}, serve_mode = #{serveMode}, serve_mode_name = #{serveModeName}, frequency = #{frequency}, frequency_name = #{frequencyName} rows_nums = #{rowsNums}
where import_detail_id = #{importDetailId}
